   //     const marker = L.marker([lat, lon]);

   //     marker.addTo(map);
    //  }
   // });

 // }
 makeTransmitterMarkers(map: L.Map): L.LayerGroup {
  const layer = L.layerGroup(); // Create a layer group to hold the markers

  this.http.get<Transmitter[]>(this.baseURL).subscribe((res: Transmitter[]) => {
    for (const t of res) {
      const lon = t.tranSouradniceS;
      const lat = t.tranSouradniceN;
      const popupContent = t.name;
      const imagePath = t.imagePath;

      const customIcon = L.icon({
        iconUrl: imagePath,
        iconSize: [32, 32],
        iconAnchor: [16, 32]
      });

      const marker = L.marker([lat, lon], { icon: customIcon });
      const circleMarker= L.marker([lat, lon], { icon: customIcon });
      marker.bindPopup(popupContent).addTo(layer);
      circleMarker.bindPopup(popupContent).addTo(layer);
    }

    // Once all markers are added, return the layer group
    return layer;
  });

  // Return an empty layer group as a fallback (in case the HTTP request hasn't completed yet)
  return layer;
}
}
