import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Route } from 'src/Route';

@Injectable({
  providedIn: 'root'
})
export class CircleMarkerService {
    private baseURL = "http://localhost:11111/route";

  constructor(private http: HttpClient) { }

  static scaledRadius(val: number, maxVal: number): number {
    return 20 * (val / maxVal);
  }


  makeRouteCircleMarkers(map: L.Map): L.LayerGroup {
    const layer = L.layerGroup(); // Create a layer group to hold the markers

    this.http.get<Route[]>(this.baseURL).subscribe((res: Route[]) => {
      for (const t of res) {
        const lon = t.routeSouradniceS;
        const lat = t.routeSouradniceN;
        const circle=L.circleMarker([lat,lon]);
        circle.addTo(layer);
      }
    

    // Once all markers are added, return the layer group
    return layer;
  });
    return layer;
  // Return an empty layer group as a fallback (in case the HTTP request hasn't completed yet)
 
}
}
