import { Injectable } from '@angular/core';
import { MarkerService } from '../marker.service';
import { PolylineService } from './polyline.service';
import * as L from 'leaflet';
import { CircleMarkerService } from '../circleMarkers.service';

@Injectable({
  providedIn: 'root'
})
export class ControllerService {
  constructor(
    private markerService: MarkerService,
    private polylineService: PolylineService,
    private circleMarkerService:CircleMarkerService
  ) {}

  createControlPanel(map: L.Map): void {
    const layerControl = L.control.layers(undefined, undefined, { collapsed: false }).addTo(map);

    const markers = this.markerService.makeTransmitterMarkers(map);
    const polyline = this.polylineService.makeRoutePolyline(map);
    //const routeMarkers=this.clickService.makeRouteMarkers(map);
    const circleMarkers=this.circleMarkerService.makeRouteCircleMarkers(map);
    
    layerControl.addOverlay(markers, "Vysilace");
    layerControl.addOverlay(polyline, "Dosah signalu");
    layerControl.addOverlay(circleMarkers,"Sila prijimaneho signalu na trase")
    
    

    markers.addTo(map);
    polyline.addTo(map);
    circleMarkers.addTo(map);

    // Add additional layers or controls if needed
  }
}
