import { AfterViewInit, Component } from '@angular/core';
import * as L from 'leaflet';
import { MarkerService } from '../marker.service';
import { PolylineService } from './polyline.service';

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent implements AfterViewInit {
  private map!: L.Map;
  private markerLayer!: L.LayerGroup;
  private coloredRoutesLayer!: L.LayerGroup;
  private singleColorRoutesLayer!: L.LayerGroup;

  constructor(private markerService: MarkerService, private polylineService: PolylineService) {}

  ngAfterViewInit(): void {
    this.initMap();
    this.initLayers();
    this.addLayersToMap();
    this.addLayerControl();
  }

  private initMap(): void {
    this.map = L.map('map').setView([51.505, -0.09], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      maxZoom: 18
    }).addTo(this.map);
  }

  private initLayers(): void {
    this.markerLayer = L.layerGroup();
    const markers = this.markerService.makeTransmitterMarkers(this.map);
    this.markerLayer.addLayer(markers);

    this.coloredRoutesLayer = L.layerGroup();
    const coloredRoutes = this.polylineService.makeRoutePolyline(this.map);
    this.coloredRoutesLayer.addLayer(coloredRoutes);

    this.singleColorRoutesLayer = L.layerGroup();
    const singleColorRoutes = this.polylineService.makeRoutePolylineBasic(this.map);
    this.singleColorRoutesLayer.addLayer(singleColorRoutes);
  }

  private addLayersToMap(): void {
    this.markerLayer.addTo(this.map);
    this.coloredRoutesLayer.addTo(this.map);
    this.singleColorRoutesLayer.addTo(this.map);
  }

  private addLayerControl(): void {
    const baseLayers = {
      'Base Layer': L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
        maxZoom: 18
      })
    };

    const overlays = {
      'Markers': this.markerLayer,
      'Colored Routes': this.coloredRoutesLayer,
      'Single-Color Routes': this.singleColorRoutesLayer
    };

    L.control.layers(baseLayers, overlays).addTo(this.map);
  }
}
