 //     for (const c of res.features) {
  //      const lon = c.geometry.coordinates[0];
   //     const lat = c.geometry.coordinates[1];
   //     const marker = L.marker([lat, lon]);

   //     marker.addTo(map);
    //  }
   // });

 // }
 makeTransmitterMarkers(map: L.Map): void {
  this.http.get<Transmitter[]>(this.baseURL).subscribe((res: Transmitter[]) => {
    for (const t of res) {
      const lon = t.tranSouradniceS;
      const lat = t.tranSouradniceN;
      const popupContent=t.name;
      const color=t.tranColour;

      var myIcon = L.icon({
        iconUrl: 'my-icon.png',
        iconSize: [38, 95],
        iconAnchor: [22, 94],
        popupAnchor: [-3, -76],
        shadowSize: [68, 95],
        shadowAnchor: [22, 94]
        });
      const marker = L.marker([lat, lon],{icon: myIcon});
       marker.bindPopup(popupContent);

      marker.addTo(map);
    }
  });
}
}
