import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Transmitter } from 'src/Transmitter';
import { Route } from 'src/Route';
import { LatLngExpression } from 'leaflet';

@Injectable({
    providedIn: 'root'
})
export class PolylineService {
 
 
  private baseURL = "http://localhost:11111/route";
  constructor(private http: HttpClient) {
}

makeRoutePolyline(map: L.Map): void {
  let path: any[] = [];

  // Dotaz na tabulku 'route'
  this.http.get<Route[]>('http://localhost:11111/route').subscribe((routes: Route[]) => {
    // Dotaz na tabulku 'transmitters'
    this.http.get<Transmitter[]>('http://localhost:11111/transmitters').subscribe((transmitters: Transmitter[]) => {
      for (const route of routes) {
        const transmitterId = route.transmitterId;
        const transmitter = transmitters.find(t => t.id === transmitterId);

        if (transmitter) {
          const lon = route.routeSouradniceS;
          const lat = route.routeSouradniceN;
          const color = transmitter.routeColor;

          path.push( [[lat, lon]]);
          L.polyline(path, { color }).addTo(map);
        }
      }
    });
  });
}
