import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Transmitter } from 'src/Transmitter';
import { Route } from 'src/Route';
import { LatLngExpression } from 'leaflet';
import { MarkerService } from '../marker.service';
import { Observable } from 'rxjs';
import { Log } from 'src/Log';

@Injectable({
    providedIn: 'root'
})
export class PolylineService {
 
 
  private baseURL = "http://localhost:11111";
  constructor(private http: HttpClient) {
}


makeRoutePolyline(map: L.Map): L.LayerGroup {
  const layer = L.layerGroup(); // Create a layer group to hold the polylines

  this.http.get<Route[]>(this.baseURL + '/route').subscribe((routes: Route[]) => {
    this.http.get<Transmitter[]>(this.baseURL + '/transmitters').subscribe((transmitters: Transmitter[]) => {
      const path: L.LatLngTuple[] = [];

      for (const route of routes) {
        const transmitterId = route.transmitterId;
        const transmitter = transmitters.find(t => t.id === transmitterId);

        if (transmitter) {
          const lon = route.routeSouradniceS;
          const lat = route.routeSouradniceN;
          path.push([lat, lon]);
        }
      }

      // Create polyline segments with colors based on transmitter ID
      for (let i = 0; i < path.length - 1; i++) {
        const segment = [path[i], path[i + 1]];
        const transmitterId = routes[i].transmitterId;
        const color = this.getColorByTransmitterId(transmitterId);
        const polyline = L.polyline(segment, { color });
        polyline.addTo(layer);

        // Fetch RSSI data from the "log" table
        const routeTime = routes[i].routTime; // Assuming "routeTime" is the time associated with the current route
        this.fetchRSSIFromLog(routeTime.toString()).subscribe((log: Log) => {
          // Add click event handler with popup
          polyline.on('click', function(e) {
            const popupContent = log.rssi.toString(); // Replace with actual information from the log
            const popup = L.popup().setLatLng(e.latlng).setContent(popupContent);
            map.openPopup(popup);
          });
        });
      }
    });
  });

  // Return the layer group containing the polylines
  return layer;
}

fetchRSSIFromLog(routeTime: string): Observable<Log> {
  const url = this.baseURL + '/log?routeTime=' + routeTime; // Assuming the API endpoint to fetch the log data
  return this.http.get<Log>(url);
}

getColorByTransmitterId(transmitterId: number): string {
  // Define your color mapping logic based on transmitter ID
  // For example:
  if (transmitterId === 1) {
    return 'red';
  } else if (transmitterId === 2) {
    return 'blue';
  } else if (transmitterId === 3) {
    return 'green';
  }
  // Return a default color if the transmitter ID doesn't match any specific mapping
  return 'black';
}
}
