import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Transmitter } from 'src/Transmitter';
import { Route } from 'src/Route';
import { LatLngExpression } from 'leaflet';

@Injectable({
    providedIn: 'root'
})
export class PolylineService {
 
 
  private baseURL = "http://localhost:11111/";
  constructor(private http: HttpClient) {
}





makeRoutePolyline(map: L.Map): void {
  this.http.get<Route[]>(this.baseURL).subscribe((res: Route[]) => {
    let path: any[] = [];

    for (const t of res) {
      const lon = t.routeSouradniceS;
      const lat = t.routeSouradniceN;
      path.push([lat, lon]);
    }

    const polyline = L.polyline(path).addTo(map);
  });
}


makeRoutePolyline1(map: L.Map): void {
  this.http.get<Route[]>(this.baseURL + '/route').subscribe((routes: Route[]) => {
    this.http.get<Transmitter[]>(this.baseURL + '/transmitters').subscribe((transmitters: Transmitter[]) => {
      const path: L.LatLngTuple[] = [];

      for (const route of routes) {
        const transmitterId = route.transmitterId;
        const transmitter = transmitters.find(t => t.id === transmitterId);

        if (transmitter) {
          const lon = route.routeSouradniceS;
          const lat = route.routeSouradniceN;
          path.push([lat, lon]);
        }
      }

      // Create polyline segments with colors based on transmitter ID
      for (let i = 0; i < path.length - 1; i++) {
        const segment = [path[i], path[i + 1]];
        const transmitterId = routes[i].transmitterId;
        const color = this.getColorByTransmitterId(transmitterId);
        L.polyline(segment, { color }).addTo(map);
      }
    });
  });
}

getColorByTransmitterId(transmitterId: number): string {
  // Define your color mapping logic based on transmitter ID
  // For example:
  if (transmitterId === 1) {
    return 'red';
  } else if (transmitterId === 2) {
    return 'blue';
  } else if (transmitterId === 3) {
    return 'green';
  }
  // Return a default color if the transmitter ID doesn't match any specific mapping
  return 'black';
}
}
