import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Transmitter } from 'src/Transmitter';
import { Route } from 'src/Route';

@Injectable({
    providedIn: 'root'
})
export class PolylineService {
 
 
  private baseURL = "http://localhost:11111/route";
  constructor(private http: HttpClient) {
}


makeRoutePolyline(map: L.Map): void {
  this.http.get<Route[]>(this.baseURL).subscribe((res: Route[]) => {
    let path: any[] = [];

    for (const t of res) {
      const lon = t.routeSouradniceS;
      const lat = t.routeSouradniceN;
      path.push([lat, lon]);
    }

    const polyline = L.polyline(path, { color: 'red' }).addTo(map);
  });
}

}
