 makeTransmitterMarkers(map: L.Map): void {
  this.http.get<Transmitter[]>(this.baseURL).subscribe((res: Transmitter[]) => {
    for (const t of res) {
      const lon = t.tranSouradniceS;
      const lat = t.tranSouradniceN;
      const popupContent = t.name;
      const imagePath = t.imagePath;

      const customIcon = L.icon({
        iconUrl: imagePath,
        iconSize: [32, 32],
        iconAnchor: [16, 32]
      });
      const marker = L.marker([lat, lon], { icon: customIcon });
      marker.bindPopup(popupContent);
      marker.addTo(map);
      const layer=L.layerGroup([marker]);
      var layerControl = L.control.layers().addTo(map);
      layerControl.addBaseLayer(layer,"Vysilace")
    }

  });

}
makeTransmitterCircleMarkers(map:L.Map):void{
  this.http.get<Transmitter[]>(this.baseURL).subscribe((res: Transmitter[]) => {
    for (const t of res) {
      const lon = t.tranSouradniceS;
      const lat = t.tranSouradniceN;

      const circle = L.circleMarker([lat, lon],);

      circle.addTo(map);
    }
  });

}
