import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Transmitter } from 'src/Transmitter';
import { style } from '@angular/animations';


@Injectable({
  providedIn: 'root'
})
export class MarkerService {
  //capitals: string = '/assets/data/usa-capitals.geojson';

  private baseURL = "http://localhost:11111/transmitters";


  constructor(private http: HttpClient) {
  }



 // makeCapitalMarkers(map: L.Map): void {
 //   this.http.get(this.capitals).subscribe((res: any) => {
 //     for (const c of res.features) {
  //      const lon = c.geometry.coordinates[0];
   //     const lat = c.geometry.coordinates[1];
   //     const marker = L.marker([lat, lon]);

   //     marker.addTo(map);
    //  }
   // });

 // }
 makeTransmitterMarkers(map: L.Map): void {
  this.http.get<Transmitter[]>(this.baseURL).subscribe((res: Transmitter[]) => {
    const layerMarkers = L.layerGroup(); // Create a layer group to hold the markers

    for (const t of res) {
      const lon = t.tranSouradniceS;
      const lat = t.tranSouradniceN;
      const popupContent = t.name;
      const imagePath = t.imagePath;

      const customIcon = L.icon({
        iconUrl: imagePath,
        iconSize: [32, 32],
        iconAnchor: [16, 32]
      });
      const marker = L.marker([lat, lon], { icon: customIcon });
      marker.bindPopup(popupContent);
      marker.addTo(layerMarkers); // Add the marker to the layer group
    }

    const circleMarkers = L.layerGroup(); // Create a layer group to hold the circle markers
    for (const t of res) {
      const lon = t.tranSouradniceS;
      const lat = t.tranSouradniceN;

      const circle = L.circleMarker([lat, lon]);
      circle.addTo(circleMarkers); // Add the circle marker to the layer group
    }

    const overlays = {
      'Icon Markers': layerMarkers,
      'Circle Markers': circleMarkers
    };

    const layerControl = L.control.layers(overlays).addTo(map); // Create a layer control with the marker layers as overlays
  });
}
}
