import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as L from 'leaflet';
import { Transmitter } from './transmitter/Transmitter';
import { TransmitterService } from './transmitter/transmitter.service';

@Injectable({
  providedIn: 'root'
})
export class MarkerService {
  capitals: string = '/assets/data/usa-capitals.geojson';
  private baseUrl="http://localhost:11111";




  constructor(private http: HttpClient) {
  }

  makeCapitalMarkers(map: L.Map): void {
    this.http.get(this.capitals).subscribe((res: any) => {
      for (const c of res.features) {
        const lon = c.geometry.coordinates[0];
        const lat = c.geometry.coordinates[1];
        const marker = L.marker([lat, lon]);

        marker.addTo(map);
      }
    });

  }
 makeTransmitterMarkers(map:L.Map):void {
       this.http.get(this.baseUrl).subscribe((res:any)=>{
        for(const t of res.Transmitter){
          const lon=t.souradniceS;
          const lat=t.souradniceN;
          const marker=L.marker([lat,lon]);
          marker.addTo(map);
        }
       });
      }
 }
